import {
    Grid,
    DialogActions,
    MuiThemeProvider,
    TextField,
    Button, IconButton,
    Icon,
    TableHead,
    TableCell,
    TableRow,
    Checkbox, InputAdornment,
    TablePagination,
    Radio,
    Dialog
  } from "@material-ui/core";
  import { createEmployee } from "./EmployeeService";
  import { createMuiTheme } from "@material-ui/core/styles";
  import React, { Component } from "react";
  import ReactDOM from "react-dom";
  import MaterialTable, {
    MTableToolbar,
    Chip,
    MTableBody,
    MTableHeader
  } from "material-table";
  import { useTranslation, withTranslation, Trans } from "react-i18next";
  import { getAllByRoot, saveItem, getAllRoles, getItemById } from "./EmployeeService";
  import { Link } from "react-router-dom";
  import DateFnsUtils from "@date-io/date-fns";
  import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
  import DialogContent from "@material-ui/core/DialogContent";
  import DialogTitle from "@material-ui/core/DialogTitle";
  import Input from "@material-ui/core/Input";
  import InputLabel from "@material-ui/core/InputLabel";
  import MenuItem from "@material-ui/core/MenuItem";
  import FormControl from "@material-ui/core/FormControl";
  import Select from "@material-ui/core/Select";
  import AsynchronousAutocomplete from "../utilities/AsynchronousAutocomplete";
  import Draggable from "react-draggable";
  import Paper from "@material-ui/core/Paper";
  import NotificationPopup from "../Component/NotificationPopup/NotificationPopup";
  import Autocomplete from "@material-ui/lab/Autocomplete";
  import { toast } from "react-toastify";
  import "react-toastify/dist/ReactToastify.css";
  import '../../../styles/views/_style.scss';
  toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3
  });
  class EmployeeDialog extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        name: '',
        email: '',
        phone: '',
        code:'',
        id:'',
        age:'',
        commune:'',
        district:'',
        province:'',
        createDate:'',
        createdBy:'',
        modifieldBy:'',
        modifyDate:'',
        voided:''

      };
    }
  
    handleChange = (event) => {
      this.setState({ [event.target.name]: event.target.value });
    };
    
    handleSubmit = (event) => {
      console.log("kakhanh")
      event.preventDefault();
      const employee = {
        name: this.state.name,
        email: this.state.email,
        phone: this.state.phone,
        id: this.state.id,
        commune: this.state.commune,
        code: this.state.code,
        age:this.state.age,
        district:this.state.district,
        province:this.state.province,
        createDate:this.state.createDate,
        createdBy:this.state.createdBy,
        modifieldBy:this.state.modifieldBy,
        modifyDate:this.state.modifyDate,
        voided: this.state.voided

      };
      this.props.onSave(employee);
      this.props.onClose();
      this.setState({ name: '', email: '', phone: '',code:'', id:'',commune:'',age:'',district:'',province:'',createDate:'',createdBy:'',modifieldBy:'',modifyDate:'',voided:'' });
      createEmployee(employee)
      .then((response) => {
        console.log('Employee created successfully!', response);
        this.props.onSave(employee);
        this.props.onClose();
        this.setState({ name: '', email: '', phone: '',code:'', id:'',commune:'',age:'',district:'',province:'',createDate:'',createdBy:'',modifieldBy:'',modifyDate:'',voided:'' });
      })
      .catch((error) => {
        console.error('Error creating employee:', error);
        console.log('chua post dc')
      });
    };
  
    render() {
      const { open, onClose, onSave } = this.props;
      return (
        <Dialog open={open} onClose={onClose}>
          <DialogTitle>Add Employee</DialogTitle>
          <form>
          <DialogContent>
           
              <TextField label="Name" name="name" value={this.state.name} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Email" name="email" value={this.state.email} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Phone" name="phone" value={this.state.phone} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Code" name="code" value={this.state.code} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Commune" name="commune" value={this.state.commune} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Id" name="id" value={this.state.id} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Age" name="age" value={this.state.age} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="District" name="district" value={this.state.district} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Province" name="province" value={this.state.province} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="ModifieldBy" name="modifieldBy" value={this.state.modifieldBy} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="ModifyDate" name="modifyDate" value={this.state.modifyDate} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="CreateDate" name="createDate" value={this.state.province} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Province" name="province" value={this.state.province} onChange={this.handleChange} fullWidth margin="normal" required />
              <TextField label="Province" name="province" value={this.state.province} onChange={this.handleChange} fullWidth margin="normal" required />


          </DialogContent>
          <DialogActions>
            <Button onClick={onClose}>Cancel</Button>
            <Button onClick = {this.handleSubmit}  color="primary" form="employee-form">Save</Button>
          </DialogActions>
          </form>
        </Dialog>
      );
    }
  }
  
  export default EmployeeDialog;
