import React,{ useState, useEffect } from "react";
import './MenuEmployee.css';
import { searchEmployee } from "./MenuEmployeeService"; 
class MenuEmployee extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          employees: [],
        };
      };
      async componentDidMount() {
        const [employeeList, setEmployeeList] = useState([]);
        useState(() => {
          const searchObject = {
            name:'',
            id:'',
            email:'',
            age:''
          };
          searchEmployee({})
          .then(res =>{
            console.log(res.data.data);
            this.setState(res.data.data);
          }) // assume we have an employee ID
        
        })
        
      }
    render(){
        const { employeeList } = this.state;
        return(
            <div>
                <div className="flex-container">
                <ul>
                    <li><a href="#news">Employee</a></li>
                    <li><a href="#contact">AddEmployee</a></li>
                    <li><a href="#about">About</a></li>
                </ul>
                </div>
                <div className="flex-container">
                <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên nhân viên</th>
            <th>Tuổi</th>
            <th>Địa chỉ</th>
          </tr>
        </thead>
        <tbody>
          {employeeList.map(employee => (
            <tr key={employee.id}>
              <td>{employee.id}</td>
              <td>{employee.name}</td>
              <td>{employee.age}</td>
              <td>{employee.address}</td>
            </tr>
          ))}
        </tbody>
      </table>
                </div> 
            </div>
        )
    }
}
export default MenuEmployee;
