import './MenuEmployee.css';
import React,  { useState, useEffect }  from 'react';
import { searchEmployee } from './MenuEmployeeService';

class MenuEmployee extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      employeeList: [],
    };
  }
  
  componentDidMount() {
    const searchObject = {
      name: '', // Tên nhân viên cần tìm kiếm
      employeeId: '', // Mã nhân viên cần tìm kiếm
      email: '', // Địa chỉ email cần tìm kiếm
    };

    const url = 'http://training-api.oceantech.com.vn/cms/employees/search';

    searchEmployee(url, searchObject)
      .then(response => {
        this.setState({ employeeList: response.data });
        console.log("Khanh");
      })
      .catch(error => {
        console.log(error);
        console.log("loi")
      });
  }

  render() {
    const { employeeList } = this.state;
    return (
      <div>
        {employeeList.map((employee) => (
          <div key={employee.id}>
            <span>{employee.name}</span>
            <span>{employee.employeeId}</span>
            <span>{employee.email}</span>
          </div>
        ))}
      </div>
    );
  }   
}

export default MenuEmployee;
