import {
    Grid,
    TextField,
    IconButton,
    Icon,
    Button,
    TableHead,
    TableCell,
    TableRow,
    Checkbox,
    TablePagination,
    Tooltip,
    FormControl,
    Input, InputAdornment,
  } from '@material-ui/core'
  import { createMuiTheme } from '@material-ui/core/styles'
  import React, { Component,useState, useEffect } from 'react'
  import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator'
  import MaterialTable, {
    MTableToolbar,
    Chip,
    MTableBody,
    MTableHeader,
  } from 'material-table'
  import { useTranslation, withTranslation, Trans } from 'react-i18next'
  import {
    getFlatRootChild,
    getAllByRoot,
    deleteItem,
    searchEmployee,
    createEmployee,
    saveItem,
    getItemById,
    getAllItem,
    deleteCheckItem
  } from './EmployeeService'
  // import SupplierDialog from './EmployeeDialog'
  import { Breadcrumb, ConfirmationDialog } from 'egret'
  import { Helmet } from 'react-helmet'
  import { withStyles } from '@material-ui/core/styles'
  import SearchIcon from '@material-ui/icons/Search'; 
  import { Link } from "react-router-dom";
  import NotificationPopup from '../Component/NotificationPopup/NotificationPopup'
  const LightTooltip = withStyles((theme) => ({
    tooltip: {
      backgroundColor: theme.palette.common.white,
      color: 'rgba(0, 0, 0, 0.87)',
      boxShadow: theme.shadows[1],
      fontSize: 11,
      position: "absolute",
      top: '-15px',
      left: '-30px',
      width: '80px'
    }
  }))(Tooltip);
  function MaterialButton(props) {
    const { t, i18n } = useTranslation()
    const item = props.item
    return (
      <div className="none_wrap">
        <LightTooltip title={t('general.editIcon')} placement="right-end" enterDelay={300} leaveDelay={200}>
          <IconButton size="small" onClick={() => props.onSelect(item, 0)}>
            <Icon fontSize="small" color="primary">edit</Icon>
          </IconButton>
        </LightTooltip>
        <LightTooltip title={t('general.deleteIcon')} placement="right-end" enterDelay={300} leaveDelay={200}>
          <IconButton size="small" onClick={() => props.onSelect(item, 1)}>
            <Icon fontSize="small" color="error">delete</Icon>
          </IconButton>
        </LightTooltip>
      </div>
    )
  }
  class Employee extends React.Component {
    state = {
      openDialog: false,
      rowsPerPage: 5,
      page: 0,
      data: [],
      totalElements: 0,
      itemList: [],
      shouldOpenEditorDialog: false,
      shouldOpenConfirmationDialog: false,
      shouldOpenConfirmationDeleteAllDialog: false,
      keyword: '',
      shouldOpenNotificationPopup: false,
      Notification: ''
    }
    constructor(props) {
        super(props);
        this.state = {
          employees: [],
          showForm: false,
          name: "",
          email: "",
          age: "",
          address: "",
          code:"",
          commune:"",
          district:"",
          province:""
         
          
        };
      this.handleTextChange = this.handleTextChange.bind(this)
    }
    handleTextChange(event) {
      this.setState({ keyword: event.target.value })
    }
    handleOpenDialog = () => {
      this.setState({ openDialog: true });
    };
    
    handleCloseDialog = () => {
      this.setState({ openDialog: false });
    };
    handleKeyDownEnterSearch = (e) => {
      if (e.key === 'Enter') {
        this.search()
      }
    }
    async componentDidMount() {
        const searchObject = {
            name:'',
            id:'',
            email:'',
            age:'',
            code:'',
            phone:''
          }; // assume we have an employee ID
          const employee = await searchEmployee(searchObject);
          this.setState({ employee });
          console.log("khanh");
          searchEmployee({})
            .then(res =>{
              console.log(res.data.data);
              this.setState({
                employees:res.data.data
              });
            })
      this.updatePageData()
    }
    getListItemChild(item) {
      var result = [];
      var root = {};
      root.name = item.name;
      root.description = item.description;
      root.code = item.code;
      root.icon = item.icon;
      root.path = item.path;
      root.id = item.id;
      root.parentId = item.parentId;
      result.push(root);
      if (item.children) {
        item.children.forEach(child => {
          var childs = this.getListItemChild(child);
          result.push(...childs);
        });
      }
      return result;
    }
    updatePageData = () => {
      var searchObject = {}
      searchObject.keyword = ''
      searchObject.pageIndex = this.state.page + 1
      searchObject.pageSize = this.state.rowsPerPage
      getAllByRoot().then(({ data }) => {
        var itemList = [...data];
        var list = [];
        itemList.forEach(item => {
          var items = this.getListItemChild(item);
          list.push(...items);
        });
        this.setState({ itemList: list });
      })
    }
    setPage = (page) => {
      this.setState({ page }, function () {
        this.updatePageData()
      })
    }
  
    setRowsPerPage = (event) => {
      this.setState({ rowsPerPage: event.target.value, page: 0 }, function () {
