import {
    Grid,
    DialogActions,
    MuiThemeProvider,
    TextField,
    Button, IconButton,
    Icon,
    TableHead,
    TableCell,
    TableRow,
    Checkbox, InputAdornment,
    TablePagination,
    Radio,
    Dialog
  } from "@material-ui/core";
  import { createMuiTheme } from "@material-ui/core/styles";
  import React, { Component } from "react";
  import ReactDOM from "react-dom";
  import MaterialTable, {
    MTableToolbar,
    Chip,
    MTableBody,
    MTableHeader
  } from "material-table";
  import { useTranslation, withTranslation, Trans } from "react-i18next";
  import { getAllByRoot, saveItem, getAllRoles, getItemById } from "./EmployeeService";
  import { Link } from "react-router-dom";
  import DateFnsUtils from "@date-io/date-fns";
  import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
  import DialogContent from "@material-ui/core/DialogContent";
  import DialogTitle from "@material-ui/core/DialogTitle";
  import Input from "@material-ui/core/Input";
  import InputLabel from "@material-ui/core/InputLabel";
  import MenuItem from "@material-ui/core/MenuItem";
  import FormControl from "@material-ui/core/FormControl";
  import Select from "@material-ui/core/Select";
  import AsynchronousAutocomplete from "../utilities/AsynchronousAutocomplete";
  import Draggable from "react-draggable";
  import Paper from "@material-ui/core/Paper";
  import NotificationPopup from "../Component/NotificationPopup/NotificationPopup";
  import Autocomplete from "@material-ui/lab/Autocomplete";
  import { toast } from "react-toastify";
  import "react-toastify/dist/ReactToastify.css";
  import '../../../styles/views/_style.scss';
  toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3
  });

  const EmployeeDialog = ({ open, onClose, onSave, newEmployee, onChange }) => {
    return (
      <Dialog open={open} onClose={onClose}>
        <DialogTitle>Add new employee</DialogTitle>
        <DialogContent>
          <TextField label="Name" value={newEmployee.name} onChange={onChange('name')} fullWidth />
          <TextField label="Age" value={newEmployee.age} onChange={onChange('age')} fullWidth />
          <TextField label="Position" value={newEmployee.position} onChange={onChange('position')} fullWidth />
          <TextField label="Salary" value={newEmployee.salary} onChange={onChange('salary')} fullWidth />
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>Cancel</Button>
          <Button onClick={onSave} color="primary">Save</Button>
        </DialogActions>
      </Dialog>
    );
  };
  export default EmployeeDialog;
